    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        Sign_up.setVisible(false);
        String pas = new String(jPasswordField1.getPassword());
        String em = jTextField1.getText();
        
        try {
            conn = DriverManager.getConnection(url,userid,pwd);
            stmt = conn.createStatement();
            String query = "select email,password,name from login_info where email = '"+em+"';";
            rs = stmt.executeQuery(query);
            rs.first();
            do{
            if(rs.getString("email").equals(em))
            {
                if(rs.getString("password").equals(pas) ){
                 
                 mainwindow2.setVisible(true);
                 this.setVisible(false);
                 jButton1.setBackground(new Color(0,0,0));
                 String name = rs.getString("name").toUpperCase();
                 welcome_label.setText("Welcome "+name+".");
                }
                else{
                    er2.setVisible(true);
                    JOptionPane.showMessageDialog(null,"Please Enter a valid Password");
                }
                
            }
            }
        while(rs.next());
              
            
          
            jTextField1.setText(em);
            jPasswordField1.setText("Password");
            rs.close();
            stmt.close();
            conn.close();
            
        } catch (Exception e) {
           
        }
       
    }//GEN-LAST:event_jButton1ActionPerformed

