    private void flight_fromPopupMenuWillBecomeInvisible(javax.swing.event.PopupMenuEvent evt) {//GEN-FIRST:event_flight_fromPopupMenuWillBecomeInvisible
        // TODO add your handling code here:
        if("Select Source".equals(flight_from.getSelectedItem()))
        {
            
        }
        else
        {
        if("Select Destination".equals(flight_to.getSelectedItem()))
        {
        flight_to.removeAllItems();
        String from = flight_from.getSelectedItem().toString();
        flight_to.addItem("Select Destination");
        try 
                {
                    conn = DriverManager.getConnection(url,userid,pwd);
                    stmt = conn.createStatement();
                    
                    String query = "select destination from flight_info where source = '"+from+"' group by destination;";
                    
                    rs= stmt.executeQuery(query);
                    
                     if(rs.first())
                    {
                        do
                        {
                            String place = rs.getString("destination");
                            flight_to.addItem(place);
                        }while(rs.next());
                    }
                    else
                    {
                        JOptionPane.showMessageDialog(null, "There are no flights from this source.");
                    }
                    flight_to.setSelectedItem("Select Destination");
                    
                }catch(Exception e)
                {
                    JOptionPane.showMessageDialog(null, e.getMessage());
                }
        }
        else{
            
            }
        }
    }//GEN-LAST:event_flight_fromPopupMenuWillBecomeInvisible

