    private void jButton21ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton21ActionPerformed
        // TODO add your handling code here:
        try {
       
        double pas = Double.parseDouble(txseats.getText());
        
        String name = pasname.getText(), age = pasage.getText();
        DefaultTableModel dtm;
        dtm = (DefaultTableModel) jTable4.getModel();
              
        int count = dtm.getRowCount(),b = count+1;
        if(name.equals("")||age.equals(""))
        {
            if(age.equals(""))
                JOptionPane.showMessageDialog(null, "Please enter valid age.");
            else if(name.equals(""))
                JOptionPane.showMessageDialog(null, "Please enter a valid name.");
            else
                JOptionPane.showMessageDialog(null, "Please enter valid name and age.");
        }
        
        else
        {
            if((count)>=pas-1)
            {
                passenger_enter.dispose();
                ticket_window.setVisible(true);
                dtm.addRow(new Object[] {b,name,age});
            }
            else
            {
                JOptionPane.showMessageDialog(null, "Please enter details for the next passenger");
                dtm.addRow(new Object[] {b,name,age});
                pasname.setText("");
                pasage.setText("");
            }
        }
        }catch(Exception e)
            {
                JOptionPane.showMessageDialog(null, e.getMessage());
            }
    }//GEN-LAST:event_jButton21ActionPerformed

