    private void check_outPropertyChange(java.beans.PropertyChangeEvent evt) {//GEN-FIRST:event_check_outPropertyChange
        // TODO add your handling code here:
        try{
        
       
        Date date1 = check_in.getDate();
            date1.setDate(date1.getDate()+1);
            if(check_out.getDate().before(date1))
            {
                check_out.setDate(date1);
            }
        }
        
        catch(Exception e){}
        try{
            
            
            DateFormat df = new SimpleDateFormat("yyyy-MM-dd");
            String a = df.format(check_in.getDate());
            
            int b = Integer.parseInt(a.substring(0, 4));
            int c = check_in.getDate().getMonth()+1;
            if(c<=2)
            {
                c=c+12;
                b=b-1;
            }
            int d = check_in.getDate().getDate();
            int in = (146097*b)/400 + (153*c + 8)/5 + d;
            
            
            String p = df.format(check_out.getDate());
            int q = Integer.parseInt(p.substring(0, 4));
            int r = check_out.getDate().getMonth()+1;
            if(r<=2)
            {
                r=r+12;
                q=q-1;
            }
            int s = check_out.getDate().getDate();
            int out = (146097*q)/400 + (153*r + 8)/5 + s;
            
            days2 = out-in;
            
            int jk =jTable2.getSelectedRow();
            price34 = Double.parseDouble(jTable2.getValueAt(jk, 2).toString())*days2;
            jLabel84.setText(price34*Double.parseDouble(jTextField5.getText())+"");
            
            jTextField5.grabFocus();
        }catch(Exception e){}
    }//GEN-LAST:event_check_outPropertyChange

